/* ------------ P A C K A G E S ------------ */
const Jimp = require("jimp");

/* ------------ F U N C T I O N S ------------ */
module.exports = {

    centerCnvText: function (ctx, text, y, canvas = null) {
        let tex = ctx.measureText(text).width;
        let x = 512 - (tex / 2)
        return !canvas ? ctx.fillText(text, x, y) : ctx.fillText(text, (canvas.width / 2) - (tex / 2), y);
    }

}